mapper.select:
select  
from bas_customer
where enterprise_id=#{enterpriseId,jdbcType=VARCHAR}
and customer_type=#{customerType,jdbcType=VARCHAR}


		and customerid in (

			#{customerSet.id,jdbcType=VARCHAR}

		)

select * from (SELECT a.CUSTOMERID,
a.FOLLOWUP, a.receiving_address_id, a.enterprise_id, a.CUSTOMER_TYPE, a.DESCR_C, a.ACTIVE_FLAG, b.operate_type, b.contract_url, b.client_no, b.client_content, b.client_start_date, b.client_end_date, b.client_term, b.is_chinese_label, c.enterprise_no, c.shorthand_name, c.enterprise_name, c.contacts, c.contacts_phone, c.remark FROM bas_customer a left join gsp_customer b on a.enterprise_id = b.enterprise_id left join gsp_enterprise_info c on c.enterprise_id= b.enterprise_id) d
${limitClause}
