mapper.select:
SELECT t_asn_h.*, t_asn_d.lotatt03, type_code.codename_c AS asntypeName, status_code.codename_c AS asnstatusName, release_code.codename_c AS releasestatusName, cold_code.codename_c AS coldTag, t_supplier.DESCR_C AS gysName, t_line.`name` AS lineName FROM doc_asn_header t_asn_h LEFT JOIN bas_codes type_code ON type_code.code = t_asn_h.asntype LEFT JOIN bas_codes status_code ON status_code.code = t_asn_h.asnstatus LEFT JOIN bas_codes release_code ON release_code.code = t_asn_h.releasestatus LEFT JOIN doc_asn_details t_asn_d ON t_asn_h.asnno = t_asn_d.asnno AND t_asn_d.asnlineno = 1 LEFT JOIN bas_sku t_sku ON t_sku.customerid = t_asn_d.customerid AND t_sku.sku = t_asn_d.sku LEFT JOIN bas_codes cold_code ON cold_code.code = t_sku.reservedfield07 AND cold_code.codeid = 'LL_TYP' LEFT JOIN product_line t_line ON t_line.product_line_id = t_sku.sku_group1 LEFT JOIN bas_customer t_supplier ON t_supplier.customerid = t_asn_h.supplierid
AND type_code.codeid = 'ASN_TYP' AND status_code.codeid = 'ASN_STS' AND release_code.codeid = 'RLS_STS'
order by ${orderByClause}
${limitClause}

	select * from (
	select a.*, b.codename_c as linestatusName, row_number() over(order by a.asnlineno) as row_num
	from (select * from Doc_Asn_Details where asnno = #{condition.asnno,jdbcType=VARCHAR}) a,
    Bas_Codes b
	where a.linestatus = b.code
	and b.codeid = 'ASN_STS'
	)
	where 1 = 1

		order by  ${orderByClause}


       ${limitClause}

mapper.update:
