mapper.select:
(SELECT aa.`asnno` documentNo,DATE_FORMAT(e.`lotatt03`,'%Y-%m-%d') soasndate,bb1.codename_c documentType,a.`customerid`,a.`sku`,e.`lotatt12`, s.`descr_c` descrc,b.PUTWAYQTY_COMPLETED asnqty,NULL soqty, bb.codename_c uom,e.`lotatt04`,e.`lotatt05`,aa.`notes`,p.`qty1`,'上海仓' warehouse FROM doc_asn_details a LEFT JOIN doc_asn_header aa ON aa.asnno=a.asnno LEFT JOIN doc_pa_details b ON aa.`asnno` = b.`asnno` AND a.asnlineno = b.ASNLINENO LEFT JOIN `inv_lot_att` e ON e.`lotnum`=b.`lotnum` LEFT JOIN `bas_sku` s ON s.`sku`=a.`sku` AND s.`customerid`=a.`customerid` LEFT JOIN `bas_package` p ON s.`packid`=p.`packid` LEFT JOIN `bas_customer` r ON s.`customerid`=r.`customerid` LEFT JOIN(SELECT * FROM `bas_codes` WHERE codeid='uom') bb ON s.defaultreceivinguom=bb.code LEFT JOIN(SELECT * FROM bas_codes WHERE codeid='ASN_TYP') bb1 ON bb1.code=aa.asntype WHERE aa.asnstatus='99'
${limitClause}
) UNION ALL (SELECT aa.`orderno` documentNo,DATE_FORMAT(aa.`edittime`,'%Y-%m-%d') soasndate,bb1.codename_c documentType,a.`customerid`,a.`sku`,e.lotatt12, s.`descr_c` descrc,NULL asnqty,b.qty soqty, bb.codename_c uom,e.lotatt04,e.lotatt05,aa.`notes`,p.`qty1`,'上海仓' warehouse FROM `doc_order_details` a LEFT JOIN `doc_order_header` aa ON aa.orderno=a.`orderno` LEFT JOIN `act_allocation_details` b ON b.`orderno`=a.`orderno` AND b.`orderlineno`=a.`orderlineno` LEFT JOIN `inv_lot_att` e ON e.`lotnum`=b.`lotnum` LEFT JOIN `bas_sku` s ON s.`sku`=b.`sku` AND s.`customerid`=b.`customerid` LEFT JOIN `bas_package` p ON s.`packid`=p.`packid` LEFT JOIN `bas_customer` r ON s.`customerid`=r.`customerid` LEFT JOIN(SELECT * FROM `bas_codes` WHERE codeid='uom') bb ON s.defaultreceivinguom=bb.code LEFT JOIN(SELECT * FROM bas_codes WHERE codeid='SO_TYP') bb1 ON bb1.code=aa.ordertype WHERE aa.sostatus='99'
${limitClause}
)

SELECT c.*,info.*,b.`DESCR_C` shippershortname,s.descr_c skudescrc,s.`reservedfield06`,p.`qty1`,bb.`DESCR_C` lotatt08Name,bc.codename_c uom FROM doc_order_packing_carton c LEFT JOIN `doc_order_packing_carton_info` info ON info.orderno=c.`orderno` AND info.`traceid`=c.`traceid` LEFT JOIN `bas_customer` b ON b.`CUSTOMERID`=c.`customerid` LEFT JOIN `bas_customer` bb ON bb.`CUSTOMERID`=c.`lotatt08` LEFT JOIN `bas_sku` s ON c.`sku`=s.`sku` AND c.`customerid`=s.`customerid` LEFT JOIN `bas_package` p ON s.`packid`=p.`packid` LEFT JOIN (select * from `bas_codes` where codeid='uom') bc ON s.defaultreceivinguom=bc.code
order by c.orderno desc,c.traceid
${limitClause}
